forwarding-options {
    port-mirroring {
        instance {
            {{port_mirror_name}} {
                family inet {
                    output {
                        ip-address {{port_mirror_collector}};
                    }
                }
            }
        }
    }
}
firewall {
    family inet {
        filter {{port_mirror_filter}} {
            term 1 {
                from {
                    source-address {
                        {{port_mirror_source_ip}};
                    }
                    destination-address {
                        {{port_mirror_destination_ip}};
                    }
                }
                then {
                    count {{port_mirror_counter}};
                    port-mirror-instance {{port_mirror_name}};
                    accept;             
                }                       
            }                           
            term 2 {                    
                then accept;            
            }                           
        }                               
    }                                   
}  
interfaces {
    {{port_mirror_input_port}} {
        unit 0 {
            family inet {
                filter {
                    input {{port_mirror_filter}};
                }
            }
        }
    }
}
